import "./App.css";
import { useRef, useState, useEffect } from "react";
import { Link } from "react-router-dom";
import axios from "axios";

const Login = () => {
  const [user, setUser] = useState("");
  const [pwd, setPwd] = useState("");

  const userRef = useRef();
  const errRef = useRef();

  const [errMsg, setErrMsg] = useState("");
  const [success, setSuccess] = useState(false);

  const [message, setMessage] = useState("");

  useEffect(() => {
    userRef.current.focus(); // Here we simply set the focus to User input.
  }, []);

  useEffect(() => {
    setErrMsg("");
  }, [user, pwd]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    axios
      .post("'http://localhost:4000/login'", { user, pwd })
      .then((response) => {
        // Handle successful login
        setMessage(response.data.message);
      })
      .catch((error) => {
        // Handle login errors
        setMessage("Login failed. Please check your credentials.");
        console.error("Error logging in:", error);
      });
  };

  return (
    <section className="login-page">
      <p
        ref={errRef}
        className={errMsg ? "errmsg" : "offscreen"}
        aria-live="assertive"
      >
        {errMsg}
      </p>
      <form className="login-form" onSubmit={handleSubmit}>
        <h1 className="topic">Login</h1>
        <label className="field-name" htmlFor="username">
          Username:
        </label>
        <input
          className="typing-boxes"
          type="text"
          id="username"
          placeholder="Enter the Username"
          ref={userRef}
          autoComplete="off"
          onChange={(e) => setUser(e.target.value)}
          required
        />

        <label className="field-name" htmlFor="password">
          Password:
        </label>
        <input
          className="typing-boxes"
          type="password" // Type "password" will not support auto-complete anyway.
          id="password"
          placeholder="Enter your password"
          onChange={(e) => setPwd(e.target.value)}
          required
        />

        <button className="submit-button">Log In</button>
        <p className="sign-up-link">
          Did not register yet?
          <br />
          <span className="line">
            <Link to="/register" className="link-btn">
              Register
            </Link>
          </span>
        </p>
      </form>
    </section>
  );
};

export default Login;
